add_dependencies(add_two_ints_client ${${PROJECT_NAME}_EXPORTED_TARGETS} ${catkin_EXPORTED_TARGETS})

target_link_libraries(add_two_ints_server
  ${catkin_LIBRARIES}
)
target_link_libraries(add_two_ints_client
  ${catkin_LIBRARIES}
)
catkin_make
# 切换到工作路径下
cd ~/catkin_ws
catkin_make
